		,KPI_FORM6_2   =  #{p.KPI_FORM6_2}
		,KPI_FORM6_3   =  #{p.KPI_FORM6_3}    
        ,KPI_STEP           = '5'
        WHERE KPI_SEQ  = #{p.KPI_SEQ}
        AND END_DATE IS NULL

