SET
 KPI_FORM7         = #{p.KPI_FORM7}
,KPI_STEP           = '6'
WHERE KPI_SEQ  = #{p.KPI_SEQ}
AND END_DATE IS NULL

          UPDATE SOCIAL_INFO_MASTER
        SET
         KPI_FORM13        = ( #{p.KPI_FORM13_1} + #{p.KPI_FORM13_2} + #{p.KPI_FORM13_3} + #{p.KPI_FORM13_4})
